import { Injectable } from '@angular/core';
import { ThemeConfig, ThemeValidationResult } from './theme.types';

/**
 * Service for validating theme configurations
 */
@Injectable({
  providedIn: 'root',
})
export class ThemeValidationService {
  /**
   * Validates a theme configuration
   * @param theme The theme configuration to validate
   * @returns Validation result with errors and warnings
   */
  validateTheme(theme: ThemeConfig): ThemeValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];

    // Check required properties
    if (!theme.name || typeof theme.name !== 'string') {
      errors.push('Theme name is required and must be a string');
    }

    if (!theme.colors || typeof theme.colors !== 'object') {
      errors.push('Theme colors are required and must be an object');
    } else {
      // Validate color properties
      const requiredColors = [
        'textBlack', 'textWhite', 'textPrimary', 'default', 'primary',
        'success', 'info', 'warning', 'danger', 'dark', 'link'
      ];

      requiredColors.forEach(color => {
        if (!theme.colors[color as keyof typeof theme.colors]) {
          errors.push(`Required color '${color}' is missing`);
        } else if (!this.isValidColor(theme.colors[color as keyof typeof theme.colors] as string)) {
          errors.push(`Color '${color}' must be a valid CSS color value`);
        }
      });
    }

    // Validate border radius
    if (typeof theme.borderRadius !== 'number' || theme.borderRadius < 0) {
      errors.push('Border radius must be a non-negative number');
    }

    // Validate border size
    if (typeof theme.borderSize !== 'number' || theme.borderSize < 0) {
      errors.push('Border size must be a non-negative number');
    }

    // Validate font size if provided
    if (theme.fontSize) {
      const fontSizes = ['small', 'medium', 'large'];
      fontSizes.forEach(size => {
        const value = theme.fontSize?.[size as keyof typeof theme.fontSize];
        if (value && !this.isValidCssSize(value)) {
          warnings.push(`Font size '${size}' should be a valid CSS size value`);
        }
      });
    }

    // Validate spacing if provided
    if (theme.spacing) {
      const spacings = ['small', 'medium', 'large'];
      spacings.forEach(size => {
        const value = theme.spacing?.[size as keyof typeof theme.spacing];
        if (value && !this.isValidCssSize(value)) {
          warnings.push(`Spacing '${size}' should be a valid CSS size value`);
        }
      });
    }

    // Check for accessibility issues
    if (theme.colors) {
      // Check contrast ratios for critical color combinations
      const contrastIssues = this.checkContrastRatios(theme.colors);
      warnings.push(...contrastIssues);
    }

    return {
      isValid: errors.length === 0,
      errors,
      warnings,
    };
  }

  /**
   * Checks if a value is a valid CSS color
   * @param color The color value to check
   * @returns True if valid, false otherwise
   */
  private isValidColor(color: string): boolean {
    // Basic validation for hex, rgb, rgba, hsl, hsla, and named colors
    const hexRegex = /^#([A-Fa-f0-9]{3}|[A-Fa-f0-9]{6}|[A-Fa-f0-9]{8})$/;
    const rgbRegex = /^rgb\((\d{1,3}),\s*(\d{1,3}),\s*(\d{1,3})\)$/;
    const rgbaRegex = /^rgba\((\d{1,3}),\s*(\d{1,3}),\s*(\d{1,3}),\s*(0|1|0?\.\d+)\)$/;
    const hslRegex = /^hsl\((\d{1,3}),\s*(\d{1,3})%,\s*(\d{1,3})%\)$/;
    const hslaRegex = /^hsla\((\d{1,3}),\s*(\d{1,3})%,\s*(\d{1,3})%,\s*(0|1|0?\.\d+)\)$/;
    const namedColors = [
      'black', 'white', 'red', 'green', 'blue', 'yellow', 'purple', 'orange',
      'pink', 'gray', 'grey', 'transparent'
    ];

    return hexRegex.test(color) ||
           rgbRegex.test(color) ||
           rgbaRegex.test(color) ||
           hslRegex.test(color) ||
           hslaRegex.test(color) ||
           namedColors.includes(color.toLowerCase()) ||
           color === 'transparent';
  }

  /**
   * Checks if a value is a valid CSS size
   * @param size The size value to check
   * @returns True if valid, false otherwise
   */
  private isValidCssSize(size: string): boolean {
    const sizeRegex = /^(\d+(\.\d+)?)(px|em|rem|vh|vw|vmin|vmax|%)$/;
    return sizeRegex.test(size);
  }

  /**
   * Checks contrast ratios for accessibility
   * @param colors The theme colors
   * @returns Array of contrast warnings
   */
  private checkContrastRatios(colors: any): string[] {
    const warnings: string[] = [];

    // This is a simplified check - in a real implementation,
    // you would use a proper color contrast calculation library
    // For now, we'll just check for obviously problematic combinations

    const lightBgColors = ['#ffffff', '#f8f9fa', '#ffffff'];
    const darkTextColors = ['#000000', '#212121'];

    // Check if light backgrounds have dark text
    if (lightBgColors.includes(colors.default) && !darkTextColors.includes(colors.textBlack)) {
      warnings.push('Light background colors should use dark text for better contrast');
    }

    return warnings;
  }

  /**
   * Merges two theme configurations with validation
   * @param baseTheme The base theme
   * @param overrideTheme The theme to override with
   * @returns Merged theme or null if invalid
   */
  mergeThemes(baseTheme: ThemeConfig, overrideTheme: Partial<ThemeConfig>): ThemeConfig | null {
    const merged = { ...baseTheme };

    if (overrideTheme.colors) {
      merged.colors = { ...merged.colors, ...overrideTheme.colors };
    }

    if (overrideTheme.borderRadius !== undefined) {
      merged.borderRadius = overrideTheme.borderRadius;
    }

    if (overrideTheme.borderSize !== undefined) {
      merged.borderSize = overrideTheme.borderSize;
    }

    if (overrideTheme.fontFamily) {
      merged.fontFamily = overrideTheme.fontFamily;
    }

    if (overrideTheme.fontSize) {
      merged.fontSize = { ...merged.fontSize, ...overrideTheme.fontSize };
    }

    if (overrideTheme.spacing) {
      merged.spacing = { ...merged.spacing, ...overrideTheme.spacing };
    }

    const validation = this.validateTheme(merged);
    return validation.isValid ? merged : null;
  }
}

import { Injectable, Inject, Optional, InjectionToken } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { ThemeConfig, ThemeName, ThemeContext } from './theme.types';
import { LIGHT_THEME, getThemeByName } from './default-themes';
import { ThemeValidationService } from './theme-validation.service';

/**
 * Injection token for theme configuration
 */
export const THEME_CONFIG = new InjectionToken<Partial<ThemeConfig>>('THEME_CONFIG');

/**
 * Service for managing global theme state and switching
 */
@Injectable({
  providedIn: 'root',
})
export class ZTThemeService {
  private currentThemeSubject = new BehaviorSubject<ThemeConfig>(LIGHT_THEME);
  private themeOverridesSubject = new BehaviorSubject<Partial<ThemeConfig> | null>(null);

  /**
   * Observable for the current global theme
   */
  public currentTheme$: Observable<ThemeConfig> = this.currentThemeSubject.asObservable();

  /**
   * Observable for theme overrides
   */
  public themeOverrides$: Observable<Partial<ThemeConfig> | null> = this.themeOverridesSubject.asObservable();

  constructor(
    private validationService: ThemeValidationService,
    @Optional() @Inject(THEME_CONFIG) private config?: Partial<ThemeConfig>
  ) {
    this.initializeTheme();
  }

  /**
   * Gets the current global theme
   */
  get currentTheme(): ThemeConfig {
    return this.currentThemeSubject.value;
  }

  /**
   * Gets the current theme overrides
   */
  get themeOverrides(): Partial<ThemeConfig> | null {
    return this.themeOverridesSubject.value;
  }

  /**
   * Initializes the theme from configuration or defaults
   */
  private initializeTheme(): void {
    if (this.config) {
      const mergedTheme = this.validationService.mergeThemes(LIGHT_THEME, this.config);
      if (mergedTheme) {
        this.setTheme(mergedTheme);
      } else {
        console.warn('Invalid theme configuration provided, falling back to default theme');
        this.setTheme(LIGHT_THEME);
      }
    } else {
      this.setTheme(LIGHT_THEME);
    }
  }

  /**
   * Sets the global theme
   * @param theme The theme configuration to set
   */
  setTheme(theme: ThemeConfig): void {
    const validation = this.validationService.validateTheme(theme);

    if (!validation.isValid) {
      console.error('Invalid theme configuration:', validation.errors);
      throw new Error(`Theme validation failed: ${validation.errors.join(', ')}`);
    }

    if (validation.warnings.length > 0) {
      console.warn('Theme validation warnings:', validation.warnings);
    }

    this.currentThemeSubject.next(theme);
    this.applyThemeToDocument(theme);
  }

  /**
   * Sets the theme by name
   * @param themeName The predefined theme name
   */
  setThemeByName(themeName: ThemeName): void {
    const theme = getThemeByName(themeName);
    this.setTheme(theme);
  }

  /**
   * Updates the current theme with partial overrides
   * @param overrides Partial theme configuration
   */
  updateTheme(overrides: Partial<ThemeConfig>): void {
    const mergedTheme = this.validationService.mergeThemes(this.currentTheme, overrides);
    if (mergedTheme) {
      this.setTheme(mergedTheme);
    } else {
      console.error('Failed to merge theme overrides');
    }
  }

  /**
   * Sets temporary theme overrides for a component
   * @param overrides Theme overrides
   * @returns Function to clear overrides
   */
  setThemeOverrides(overrides: Partial<ThemeConfig>): () => void {
    this.themeOverridesSubject.next(overrides);
    return () => this.clearThemeOverrides();
  }

  /**
   * Clears theme overrides
   */
  clearThemeOverrides(): void {
    this.themeOverridesSubject.next(null);
  }

  /**
   * Gets the effective theme context for a component
   * @param componentOverrides Optional component-specific overrides
   * @returns Theme context with global theme and overrides
   */
  getThemeContext(componentOverrides?: Partial<ThemeConfig>): ThemeContext {
    return {
      globalTheme: this.currentTheme,
      componentOverrides: componentOverrides || this.themeOverrides || undefined,
    };
  }

  /**
   * Applies theme CSS custom properties to the document root
   * @param theme The theme to apply
   */
  private applyThemeToDocument(theme: ThemeConfig): void {
    if (typeof document === 'undefined') return;

    const root = document.documentElement;
    const colors = theme.colors;

    // Apply color variables
    Object.entries(colors).forEach(([key, value]) => {
      const cssVar = `--zt-${key.replace(/([A-Z])/g, '-$1').toLowerCase()}`;
      root.style.setProperty(cssVar, value);
    });

    // Apply other theme properties
    root.style.setProperty('--zt-border-radius', `${theme.borderRadius}px`);
    root.style.setProperty('--zt-border-size', `${theme.borderSize}px`);

    if (theme.fontFamily) {
      root.style.setProperty('--zt-font-family', theme.fontFamily);
    }

    if (theme.fontSize) {
      root.style.setProperty('--zt-font-size-small', theme.fontSize.small);
      root.style.setProperty('--zt-font-size-medium', theme.fontSize.medium);
      root.style.setProperty('--zt-font-size-large', theme.fontSize.large);
    }

    if (theme.spacing) {
      root.style.setProperty('--zt-spacing-small', theme.spacing.small);
      root.style.setProperty('--zt-spacing-medium', theme.spacing.medium);
      root.style.setProperty('--zt-spacing-large', theme.spacing.large);
    }

    // Set theme class on body for CSS selectors
    document.body.className = document.body.className.replace(/theme-\w+/g, '');
    document.body.classList.add(`theme-${theme.name}`);
  }

  /**
   * Resets theme to default
   */
  resetToDefault(): void {
    this.setTheme(LIGHT_THEME);
  }

  /**
   * Toggles between light and dark themes
   */
  toggleDarkMode(): void {
    const currentName = this.currentTheme.name;
    if (currentName === 'light') {
      this.setThemeByName('dark');
    } else if (currentName === 'dark') {
      this.setThemeByName('light');
    } else {
      // For other themes, toggle to dark if current is light-like, or light if dark-like
      this.setThemeByName('dark');
    }
  }

  /**
   * Gets available theme names
   */
  getAvailableThemes(): ThemeName[] {
    return ['light', 'dark', 'bootstrap', 'material'];
  }

  /**
   * Exports current theme configuration
   */
  exportTheme(): ThemeConfig {
    return { ...this.currentTheme };
  }

  /**
   * Imports a theme configuration
   * @param themeConfig The theme configuration to import
   */
  importTheme(themeConfig: ThemeConfig): void {
    this.setTheme(themeConfig);
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

/**
 * Toast notification configuration interface
 *
 * Defines the structure and options for individual toast notifications.
 * All properties are optional except for the message and type.
 */
export interface ZtToast {
  /** Unique identifier for the toast */
  id: string;
  /** Type of toast that determines styling and icon */
  type: 'success' | 'error' | 'info' | 'warning';
  /** Optional title for the toast */
  title?: string;
  /** Main message content */
  message: string;
  /** Auto-dismiss duration in milliseconds (0 = never auto-dismiss) */
  duration?: number;
  /** Position on screen where toast appears */
  position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center';
  /** Whether the toast can be manually dismissed by clicking */
  dismissible?: boolean;
  /** Whether to show a progress bar indicating time remaining */
  showProgress?: boolean;
}

/**
 * Toast service providing centralized management of notifications
 *
 * This service maintains the global state of all active toasts and provides
 * convenient methods for displaying different types of notifications.
 *
 * The service uses BehaviorSubject for reactive state management, allowing
 * components to subscribe to toast changes and update their UI accordingly.
 */
@Injectable({
  providedIn: 'root'
})
export class ZtToastService {
  /** Internal subject managing the current list of active toasts */
  private toasts$ = new BehaviorSubject<ZtToast[]>([]);

  /**
   * Observable stream of current toasts
   * Components can subscribe to this to reactively display toasts
   */
  get toasts(): Observable<ZtToast[]> {
    return this.toasts$.asObservable();
  }

  /**
   * Display a new toast notification
   *
   * Creates a unique toast with the provided configuration and adds it to the active toasts.
   * Automatically schedules removal based on the duration setting.
   *
   * @param toast - Toast configuration (id will be auto-generated)
   */
  show(toast: Omit<ZtToast, 'id'>): void {
    const id = Math.random().toString(36).substr(2, 9);
    const newToast: ZtToast = {
      id,
      type: toast.type,
      title: toast.title,
      message: toast.message,
      duration: toast.duration ?? 5000,
      position: toast.position || 'top-right',
      dismissible: toast.dismissible !== false,
      showProgress: toast.showProgress !== false,
    };

    const currentToasts = this.toasts$.value;
    this.toasts$.next([...currentToasts, newToast]);

    // Schedule auto-removal if duration is set
    if (newToast.duration && newToast.duration > 0) {
      setTimeout(() => {
        this.remove(id);
      }, newToast.duration);
    }
  }

  /**
   * Remove a specific toast by ID
   *
   * @param id - Unique identifier of the toast to remove
   */
  remove(id: string): void {
    const currentToasts = this.toasts$.value;
    this.toasts$.next(currentToasts.filter(toast => toast.id !== id));
  }

  /**
   * Remove all active toasts
   *
   * Useful for clearing all notifications at once, such as when navigating away from a page.
   */
  clear(): void {
    this.toasts$.next([]);
  }

  /**
   * Show a success toast notification
   *
   * @param message - Main message content
   * @param title - Optional title (defaults to 'Success')
   * @param options - Additional toast configuration options
   */
  success(message: string, title?: string, options?: Partial<ZtToast>): void {
    this.show({ ...options, type: 'success', message, title });
  }

  /**
   * Show an error toast notification
   *
   * @param message - Main message content
   * @param title - Optional title (defaults to 'Error')
   * @param options - Additional toast configuration options
   */
  error(message: string, title?: string, options?: Partial<ZtToast>): void {
    this.show({ ...options, type: 'error', message, title });
  }

  /**
   * Show an info toast notification
   *
   * @param message - Main message content
   * @param title - Optional title (defaults to 'Info')
   * @param options - Additional toast configuration options
   */
  info(message: string, title?: string, options?: Partial<ZtToast>): void {
    this.show({ ...options, type: 'info', message, title });
  }

  /**
   * Show a warning toast notification
   *
   * @param message - Main message content
   * @param title - Optional title (defaults to 'Warning')
   * @param options - Additional toast configuration options
   */
  warning(message: string, title?: string, options?: Partial<ZtToast>): void {
    this.show({ ...options, type: 'warning', message, title });
  }
}

