      border-color: var(--zt-primary, #007bff);
      border-width: 2px;

      &.hoverable:hover {
        border-color: var(--zt-primary-hover-border, #0056b3);
        box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.15);
      }
    }

    &.flat {
      &.hoverable:hover {
        background-color: rgba(0, 123, 255, 0.05);
      }
    }
  }

  &.variant-info {
    &.elevated {
      border-color: var(--zt-info, #17a2b8);
