        box-shadow: 0 8px 24px rgba(23, 162, 184, 0.25);
        border-color: var(--zt-info-hover-border, #117a8b);
      }
    }

    &.outlined {
      border-color: var(--zt-info, #17a2b8);
      border-width: 2px;

      &.hoverable:hover {
        border-color: var(--zt-info-hover-border, #117a8b);
        box-shadow: 0 0 0 3px rgba(23, 162, 184, 0.15);
      }
    }

