import {
  ChangeDetectionStrategy,
  Component,
  HostBinding,
  HostListener,
  Input,
  Output,
  EventEmitter,
  OnInit,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

/**
 * A customizable select dropdown component that supports various styles, themes, and data binding.
 * It allows users to select from a list of options with configurable display and value properties.
 *
 * ## Style-Specific Behaviors:
 *
 * - **ZT Style** (`inputStyle="zt"`): Traditional select with `value=""` placeholder option
 * - **Material Style** (`inputStyle="material"`): Material Design select with `value=""` placeholder option
 * - **Bootstrap Style** (`inputStyle="bs"`): Bootstrap-styled select with `[ngValue]="null"` placeholder option
 *
 * ## Placeholder Handling:
 *
 * - ZT & Material styles: Use `value=""` for placeholder, clear to `-1`
 * - Bootstrap style: Uses `[ngValue]="null"` for placeholder, clears to `null`
 *
 * @example
 * <!-- ZT Style (default) -->
 * <zt-select
 *   [dataSource]="options"
 *   [key]="'id'"
 *   [displayValue]="'name'"
 *   [value]="selectedValue"
 *   inputStyle="zt"
 *   placeholder="Choose an option">
 * </zt-select>
 *
 * @example
 * <!-- Material Style -->
 * <zt-select
 *   [dataSource]="options"
 *   [key]="'id'"
 *   [displayValue]="'name'"
 *   [value]="selectedValue"
 *   inputStyle="material"
 *   placeholder="Choose an option">
 * </zt-select>
 *
 * @example
 * <!-- Bootstrap Style (with proper placeholder support) -->
 * <zt-select
 *   [dataSource]="options"
 *   [key]="'id'"
 *   [displayValue]="'name'"
 *   [value]="selectedValue"
 *   inputStyle="bs"
 *   placeholder="Choose an option">
 * </zt-select>
 */
@Component({
  selector: 'zt-select',
  templateUrl: './select.component.html',
  styleUrls: ['./select.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, FormsModule],
})
export class SelectComponent implements OnInit {
  /**
   * Default message displayed in the component.
   */
  message = 'Choose an option';

  /**
   * Placeholder text displayed when no option is selected.
   *
   * **Note**: Only effective with `inputStyle="bs"`. ZT and Material styles
   * use the `message` property for their placeholder text display.
   *
   * @default 'Choose an option'
   */
  @Input() placeholder? = 'Choose an option';

  /**
   * The visual style of the select component.
   *
   * - `'zt'`: Traditional ZT styling with `value=""` placeholder option
   * - `'material'`: Material Design styling with `value=""` placeholder option
   * - `'bs'`: Bootstrap styling with `[ngValue]="null"` placeholder option (recommended for placeholder functionality)
   *
   * @default 'zt'
   */
  @Input() inputStyle?: 'zt' | 'material' | 'bs' = 'zt';

  /**
   * The theme of the select component.
   * @default 'light'
   */
  @Input() theme: 'light' | 'dark' | 'bootstrap' | 'material' = 'light';

  /**
   * The size of the select component.
   * @default 'zt-md'
   */
  @Input() size: 'zt-md' | 'zt-sm' | 'zt-lg' = 'zt-md';

  /**
   * Array of data objects to populate the select options.
   * @default []
   */
  @Input() dataSource: any[] = [];

  /**
   * The property name in the data objects to use as the unique key/identifier.
   * Used for binding the selected value.
   */
  @Input() key?: any;

  /**
   * The property name in the data objects to display as the option text.
   * @default ''
   */
  @Input() displayValue: any = '';

  /**
   * The currently selected value. Should match the key property of the selected item.
   */
  @Input() value: any;

  /**
   * Whether to show a clear button to reset the selection.
   * @default true
   */
  @Input() showClearButton = true;

  /**
   * Event emitted when the selected value changes.
   */
  @Output() valueChange = new EventEmitter<any>();

  /**
   * Dynamically applies CSS classes to the select element based on size and theme.
   * @returns A string of CSS classes.
   */
  @HostBinding('class') get selectClass(): string {
    return `${this.size} theme-${this.theme}`;
  }

  /**
   * Handles the change event when a user selects an option.
   * Updates the value property with the selected option's value.
   * @param event The change event from the select element.
   */
  @HostListener('change', ['$event'])
  onChange(event: any) {
    this.value = event.target.value;
    this.valueChange.emit(this.value);
    console.log(this.key, this.value);
  }

  /**
   * Clears the current selection by setting the value to null for bs style, -1 for others.
   */
  onClear() {
    console.log(this.value);
    // Use null for bs style (which has [ngValue]="null" placeholder), -1 for others
    this.value = this.inputStyle === 'bs' ? null : -1;
    this.valueChange.emit(this.value);
  }

  /**
   * Initializes the component and ensures placeholder is shown when no value is set.
   */
  ngOnInit() {
    // If no value is provided, set appropriate default based on input style
    // bs style uses null for placeholder, others use -1
    if (this.value === undefined) {
      this.value = this.inputStyle === 'bs' ? null : -1;
    }
  }
}

styleUrlsData.data:
@use "../../assets/variables" as vars;

// Light theme (default)
:host(.theme-light) {
  --zt-border-color: #d2d2d2;
  --zt-border-focus: #0275d8;
  --zt-border-focus-out: #f30e0e;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #9b9b9b;
  --zt-text-select: #565151;
}

// Dark theme
:host(.theme-dark) {
  --zt-border-color: #4a5568;
  --zt-border-focus: #4dabf7;
  --zt-border-focus-out: #f56565;
  --zt-bg-white: #2d3748;
  --zt-text-black: #ffffff;
  --zt-text-gray: #718096;
  --zt-text-select: #a0aec0;
}

// Bootstrap theme
:host(.theme-bootstrap) {
  --zt-border-color: #d2d2d2;
  --zt-border-focus: #007bff;
  --zt-border-focus-out: #dc3545;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #6c757d;
  --zt-text-select: #495057;
}

// Material theme
:host(.theme-material) {
  --zt-border-color: #e0e0e0;
  --zt-border-focus: #6200ee;
  --zt-border-focus-out: #f44336;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #9e9e9e;
  --zt-text-select: #616161;
}

:host {
  font-family: inherit;
  padding: 0;
  margin: 0;

  // zt select
  .box_select {
    .outline {
      border: 0;
    }

    .no-outline {
      bottom: 0;
      border-bottom: 1px solid var(--zt-border-color);
      outline: 0;
    }

    .form__group {
      position: relative;
    }

    select {
      width: 100%;
      border: 1px solid var(--zt-border-color);
      border-radius: 4px;
      box-sizing: border-box;
      padding: 10px;
    }

    .label {
      position: absolute;
      top: 0;
      bottom: 0;
      left: 16px;
      display: flex;
      align-items: center;
    }

    select,
    .label .text {
      font-family: "Segoe UI";
      font-size: vars.$font-size-input;
    }

    select:focus {
      outline: none;
      border: 2px solid var(--zt-border-focus);
    }

    select:focus + .label .text,
    :not(select[value=""]) + .label .text {
      font-size: vars.$font-size-input-label-sm;
      transform: translate(0, -150%);
      background-color: var(--zt-bg-white);
      padding-left: 4px;
      padding-right: 4px;
    }

    .label .text {
      transition: all 0.15s ease-out;
    }

    .select-message {
      color: vars.$text-gray;
    }

    .close-icon {
      position: absolute;
      top: 12px;
      right: 15px;
      border: 1px solid transparent;
      background-color: transparent;
      display: inline-block;
      vertical-align: middle;
      outline: 0;
      cursor: pointer;
    }

    .close-icon:after {
      content: "X";
      display: block;
      width: 15px;
      height: 15px;
      background-color: transparent;
      z-index: 1;
      right: 35px;
      top: 0;
      bottom: 0;
      margin: auto;
      padding: 2px;
      border-radius: 50%;
      text-align: center;
      color: var(--zt-text-black);
      font-weight: normal;
      font-size: vars.$font-size-input-label-sm;
      cursor: pointer;
    }
  }

  // material select
  .material_select {
    /* select starting stylings ------------------------------*/
    .select {
      position: relative;
      width: 100%;
    }

    .select-text {
      position: relative;
      font-family: inherit;
      background-color: transparent;
      width: 100%;
      padding: 10px 10px 10px 0;
      font-size: vars.$font-size-input;
      border-radius: 0;
      border: none;
      border-bottom: 1px solid vars.$border-color;
    }

    .select-message {
      color: var(--zt-text-gray);
    }

    .select-text option {
      color: var(--zt-text-select);
      background-color: var(--zt-bg-white);
    }

    /* Remove focus */
    .select-text:focus {
      outline: none;
      border-bottom: 1px solid #00000000;
    }

    /* Use custom arrow */
    .select .select-text {
      appearance: none;
      -webkit-appearance: none;
    }

    .select:after {
      position: absolute;
      top: 18px;
      right: 10px;
      /* Styling the down arrow */
      width: 0;
      height: 0;
      padding: 0;
      content: "";
      border-left: 6px solid transparent;
      border-right: 6px solid transparent;
      border-top: 6px solid var(--zt-border-color);
      pointer-events: none;
    }

    /* LABEL ======================================= */
    .select-label {
      color: var(--zt-text-gray);
      font-size: vars.$font-size-input;
      font-weight: normal;
      position: absolute;
      pointer-events: none;
      left: 0;
      top: 10px;
      transition: 0.2s ease all;
    }

    /* active state */
    .select-text:focus ~ .select-label,
    .select-text:valid ~ .select-label {
      color: var(--zt-border-focus);
      top: -10px;
      transition: 0.2s ease all;
      font-size: vars.$font-size-input-label-sm;
    }

    /* BOTTOM BARS ================================= */
    .select-bar {
      position: relative;
      display: block;
      width: 100%;
    }

    .select-bar:before,
    .select-bar:after {
      content: "";
      height: 2px;
      width: 0;
      bottom: 1px;
      position: absolute;
      background: var(--zt-border-focus);
      transition: 0.2s ease all;
    }

    .select-bar:before {
      left: 50%;
    }

    .select-bar:after {
      right: 50%;
    }

    /* active state */
    .select-text:focus ~ .select-bar:before,
    .select-text:focus ~ .select-bar:after {
      width: 50%;
    }

    /* HIGHLIGHTER ================================== */
    .select-highlight {
      position: absolute;
      height: 60%;
      width: 100px;
      top: 25%;
      left: 0;
      pointer-events: none;
      opacity: 0.5;
    }

    .close-icon {
      position: absolute;
      top: 10px;
      right: 20px;
      border: 1px solid transparent;
      background-color: transparent;
      display: inline-block;
      vertical-align: middle;
      outline: 0;
      cursor: pointer;
    }

    .close-icon:after {
      content: "X";
      display: block;
      width: 15px;
      height: 15px;
      background-color: transparent;
      z-index: 1;
      right: 35px;
      top: 0;
      bottom: 0;
      margin: auto;
      padding: 2px;
      border-radius: 50%;
      text-align: center;
      color: var(--zt-text-black);
      font-weight: normal;
      font-size: vars.$font-size-input-label-sm;
      cursor: pointer;
    }
  }

  // bootstrap select
  .bs_select {
    position: relative;

    .form__field {
      width: 100%;
      border: 2px solid var(--zt-text-gray);
      border-radius: 4px;
      margin: 8px 0;
      outline: none;
      padding: 8px;
      box-sizing: border-box;
      transition: 0.3s;
      color: var(--zt-text-select);
      font-size: vars.$font-size-input;
    }

    .form__field:focus {
      border-color: var(--zt-border-focus);
      box-shadow: 0 0 8px 0 var(--zt-border-focus);
    }

    option {
      min-height: 50px !important;
    }

    .close-icon {
      position: absolute;
      top: 15px;
      right: 15px;
      border: 1px solid transparent;
      background-color: transparent;
      display: inline-block;
      vertical-align: middle;
      outline: 0;
      cursor: pointer;
    }

    .close-icon:after {
      content: "X";
      display: block;
      width: 15px;
      height: 15px;
      background-color: transparent;
      z-index: 1;
      right: 35px;
      top: 0;
      bottom: 0;
      margin: auto;
      padding: 2px;
      border-radius: 50%;
      text-align: center;
      color: var(--zt-text-black);
      font-weight: normal;
      font-size: vars.$font-size-input-label-sm;
      cursor: pointer;
    }
  }
}

components.templateData:
@if (inputStyle === 'zt') {
  <div>
    <div class="box_select">
      <div class="form__group">
        <select
          id="box_select"
          aria-labelledby="label-fname"
          class=""
          #select
          [(ngModel)]="value"
          >
          <option value="" class="select-message" selected disabled>
            {{ message }}
          </option>
          @for (obj of dataSource; track obj; let i = $index) {
            <option
              [id]="key ? key : i"
              class="select-option"
              >
              {{ displayValue ? obj[displayValue] : obj }}
            </option>
          }
        </select>
        @if (showClearButton) {
          <button class="close-icon" type="reset"></button>
        }
        <label class="label" for="fname" id="label-fname">
          <div class="text">{{ placeholder }}</div>
        </label>
      </div>
    </div>
  </div>
}

@if (inputStyle === 'material') {
  <div>
    <div class="material_select">
      <div class="select select-items">
        <select class="select-text" required #select [(ngModel)]="value">
          <option value="" class="select-message" selected disabled></option>
          @for (obj of dataSource; track obj; let i = $index) {
            <option
              [id]="key ? key : i"
              class="select-option"
              >
              {{ displayValue ? obj[displayValue] : obj }}
            </option>
          }
        </select>
        @if (showClearButton) {
          <button class="close-icon" type="reset"></button>
        }
        <span class="select-highlight"></span>
        <span class="select-bar"></span>
        <label class="select-label">{{ placeholder }}</label>
      </div>
    </div>
  </div>
}
@if (inputStyle === 'bs') {
  <div>
    <div class="bs_select">
      <select
        type="text"
        class="form__field"
        id="bs_select"
        #select
        [(ngModel)]="value"
        >
        <option [ngValue]="null" class="select-message" selected disabled>
          {{ placeholder }}
        </option>
        @for (obj of dataSource; track obj; let i = $index) {
          <option
            [id]="key ? key : i"
            class="select-option"
            >
            {{ displayValue ? obj[displayValue] : obj }}
          </option>
        }
      </select>
      @if (showClearButton) {
        <button
          class="close-icon"
          type="button"
          (click)="onClear()"
        ></button>
      }
    </div>
  </div>
}

