@use "../../assets/variables" as vars;

// Light theme (default)
:host(.theme-light) {
  --zt-border-color: #d2d2d2;
  --zt-border-focus: #0275d8;
  --zt-border-focus-out: #f30e0e;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #9b9b9b;
  --zt-text-red: #f30e0e;
}

// Dark theme
:host(.theme-dark) {
  --zt-border-color: #4a5568;
  --zt-border-focus: #4dabf7;
  --zt-border-focus-out: #f56565;
  --zt-bg-white: #2d3748;
  --zt-text-black: #ffffff;
  --zt-text-gray: #718096;
  --zt-text-red: #f56565;
}

// Bootstrap theme
:host(.theme-bootstrap) {
  --zt-border-color: #d2d2d2;
  --zt-border-focus: #007bff;
  --zt-border-focus-out: #dc3545;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #6c757d;
  --zt-text-red: #dc3545;
}

// Material theme
:host(.theme-material) {
  --zt-border-color: #e0e0e0;
  --zt-border-focus: #6200ee;
  --zt-border-focus-out: #f44336;
  --zt-bg-white: #ffffff;
  --zt-text-black: #212121;
  --zt-text-gray: #9e9e9e;
  --zt-text-red: #f44336;
}

:host {
  // zt textarea

  position: relative;
  .box_textarea {
    .outline {
      border: 0;
    }

    .no-outline {
      bottom: 0;
      border-bottom: 1px solid var(--zt-border-color);
      outline: 0;
    }

    .form__group {
      position: relative;
    }

    textarea {
      height: 48px;
      width: 100%;
      border: 1px solid var(--zt-border-color);
      border-radius: 4px;
      box-sizing: border-box;
      padding: 16px;
      overflow: auto;
      padding: 5px;
    }

    .label {
      position: absolute;
      top: 16px;
      bottom: 0;
      left: 16px;
      display: block;
      align-items: center;
    }

    textarea,
    .label .text {
      font-family: "Segoe UI";
      font-size: 16px;
    }

    textarea:focus {
      outline: none;
      border: 2px solid var(--zt-border-focus);
    }

    textarea:focus + .label .text,
    :not(textarea[value=""]) + .label .text {
      font-size: 12px;
      transform: translate(0, -150%);
      background-color: var(--zt-bg-white);
      padding-left: 4px;
      padding-right: 4px;
    }

    .label .text {
      transition: all 0.15s ease-out;
    }

    small {
      color: var(--zt-text-gray);
      font-weight: bold;
      float: right;
      background-color: transparent;
    }

    .limit-max {
      color: var(--zt-text-red);
    }
  }

  // material textarea
  .material_input {
    .textarea-group {
      width: 100%;
      position: relative;
      padding-top: 50px;

      textarea {
        border: none;
        outline: none;
        resize: none;
        width: 100%;
        max-height: 100%;
        font-size: 16px;
        font-family: "Lato", sans-serif;
        line-height: 20px;
        padding: 0 8px;
        box-sizing: border-box;
        border-bottom: 1px solid black;
        transition: border 0.2s ease;
        will-change: border;
        background-color: transparent;
        &.contains-text {
          ~ label {
            color: var(--zt-border-focus);
            top: 8px;
            font-size: 16px;
          }
        }

        &:focus {
          ~ .bar {
            transform: scaleX(1);
          }
          ~ label {
            color: var(--zt-text-gray);
            top: 8px;
            font-size: 14px;
          }
        }
      }
      label {
        position: absolute;
        font-size: 16px;
        top: 24px;
        left: 8px;
        transition: all 0.2s ease;
        cursor: text;
      }
      .bar {
        width: 100%;
        height: 3px;
        background: var(--zt-border-focus);
        top: calc(100% - 7px);
        position: absolute;
        transform: scaleX(0);
        transition: transform 0.2s ease;
      }

      small {
        float: right;
        color: var(--zt-text-gray);
        font-weight: bold;
      }

      .limit-max {
        color: var(--zt-text-red);
      }
    }
  }

  // bootstrap textarea
  .bs_textarea {
    .form__field {
      width: 100%;
      border: 2px solid var(--zt-text-gray);
      border-radius: 4px;
      margin: 8px 0;
      outline: none;
      padding: 8px;
      box-sizing: border-box;
      transition: 0.3s;
    }

    .form__field:focus {
      border-color: var(--zt-border-focus);
      box-shadow: 0 0 8px 0 var(--zt-border-focus);
    }

    small {
      float: right;
      color: var(--zt-text-gray);
      font-weight: bold;
    }

    .limit-max {
      color: var(--zt-text-red);
    }
  }
}

