  --dx-datagrid-accent-color: #007bff;
}

:host(.theme-dark) {
  --dx-datagrid-border-color: #4a4a4a;
  --dx-datagrid-header-bg: #2d2d2d;
  --dx-datagrid-header-color: #ffffff;
  --dx-datagrid-header-border-color: #555555;
  --dx-datagrid-row-bg: #1a1a1a;
  --dx-datagrid-row-color: #ffffff;
  --dx-datagrid-row-hover-bg: #333333;
  --dx-datagrid-row-selected-bg: #4a5568;
  --dx-datagrid-row-alternate-bg: #252525;
  --dx-datagrid-border-size: 1px;
  --dx-datagrid-font-size: 14px;
  --dx-datagrid-font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
