  --dx-datagrid-shadow: 0 1px 3px rgba(0, 0, 0, 0.3);
  --dx-datagrid-accent-color: #4a90e2;
}

:host(.theme-bootstrap) {
  --dx-datagrid-border-color: #dee2e6;
  --dx-datagrid-header-bg: #f8f9fa;
  --dx-datagrid-header-color: #495057;
  --dx-datagrid-header-border-color: #dee2e6;
  --dx-datagrid-row-bg: #ffffff;
  --dx-datagrid-row-color: #212529;
  --dx-datagrid-row-hover-bg: #f8f9fa;
  --dx-datagrid-row-selected-bg: #e9ecef;
