:host(.theme-material) {
  --dx-datagrid-border-color: #e0e0e0;
  --dx-datagrid-header-bg: #f5f5f5;
  --dx-datagrid-header-color: #424242;
  --dx-datagrid-header-border-color: #e0e0e0;
  --dx-datagrid-row-bg: #ffffff;
  --dx-datagrid-row-color: #212121;
  --dx-datagrid-row-hover-bg: #f5f5f5;
  --dx-datagrid-row-selected-bg: #e8f5e8;
  --dx-datagrid-row-alternate-bg: #fafafa;
  --dx-datagrid-border-size: 1px;
  --dx-datagrid-font-size: 14px;
  --dx-datagrid-font-family: 'Roboto', 'Helvetica Neue', Arial, sans-serif;
  --dx-datagrid-line-height: 1.4;
